import React from 'react';
import { Typography, Container, Box, Button, makeStyles } from '@material-ui';
import { useSpring, animated } from 'react-spring';

const useStyles = makeStyles((theme) => ({
  root: {
    backgroundColor: '#fff',
    padding: theme.spacing(4),
  },
  headline: {
    marginBottom: theme.spacing(3),
    fontWeight: 'bold',
  },
  body: {
    marginBottom: theme.spacing(2),
  },
  callToAction: {
    marginTop: theme.spacing(3),
  },
  button: {
    marginRight: theme.spacing(2),
  },
}));

const AboutUs = () => {
  const classes = useStyles();

  const fadeIn = useSpring({
    from: { opacity: 0 },
    to: { opacity: 1 },
    config: { duration: 1000 },
  });

  return (
    <Container className={classes.root}>
      <animated.div style={fadeIn}>
        <Typography variant="h4" className={classes.headline}>
          SportsZone: Your Passion, Our Platform
        </Typography>
        <Typography variant="body1" className={classes.body}>
          We are passionate about sports and dedicated to creating a thriving community for athletes and fans alike.
        </Typography>
        <Typography variant="body1" className={classes.body}>
          Our mission is to empower athletes, connect fans, and foster a love for sports.
        </Typography>
        <Typography variant="body1" className={classes.body}>
          We believe in fair play, inclusivity, and excellence.
        </Typography>
        <Typography variant="body1" className={classes.body}>
          Our team is composed of experienced sports enthusiasts who are committed to providing an exceptional platform for sports management and engagement.
        </Typography>
        <Typography variant="body1" className={classes.body}>
          We are proud to support local sports by organizing community tournaments, sponsoring local teams, and promoting grassroots sports development.
        </Typography>
        <Box className={classes.callToAction}>
          <Typography variant="h6" className={classes.body}>
            Join our community! Follow us on social media, subscribe to our newsletter, or contact us for more information.
          </Typography>
          <Button variant="contained" color="primary" className={classes.button}>
            Follow Us
          </Button>
          <Button variant="contained" color="secondary" className={classes.button}>
            Subscribe
          </Button>
          <Button variant="contained" color="default" className={classes.button}>
            Contact Us
          </Button>
        </Box>
      </animated.div>
    </Container>
  );
};

export default AboutUs;
