import React, { useState } from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Button,
  Container,
  CssBaseline,
  Grid,
  IconButton,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  TextField,
  FormControl,
  InputAdornment,
} from '@mui/material';
import SportsSoccerIcon from '@mui/icons-material/SportsSoccer';
import SearchIcon from '@mui/icons-material/Search';
import MenuIcon from '@mui/icons-material/Menu';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import ExitToAppIcon from '@mui/icons-material/ExitToApp';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CloseIcon from '@mui/icons-material/Close';
import { createTheme, ThemeProvider, keyframes } from '@mui/material/styles';
import basketballImage from '../images/basketball-tournament.jpg';
import footballImage from '../images/football-championship.jpg';
import volleyballImage from '../images/volleyball-league.jpg';
import backgroundImage from '../images/background.jpg';

const flipAnimation = keyframes`
  0% {
    transform: rotateY(0deg);
  }
  100% {
    transform: rotateY(180deg);
  }
`;

const zoomAnimation = keyframes`
  0% {
    background-size: 100%;
  }
  50% {
    background-size: 105%;
  }
  100% {
    background-size: 100%;
  }
`;

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#90caf9',
    },
    secondary: {
      main: '#f48fb1',
    },
    background: {
      default: '#000',
      paper: '#121212',
    },
    text: {
      primary: '#fff',
      secondary: '#b0bec5',
    },
  },
  typography: {
    h1: {
      fontSize: '3rem',
      fontWeight: 700,
      color: '#fff',
    },
    h5: {
      fontSize: '1.5rem',
      fontWeight: 400,
      color: '#fff',
    },
    body1: {
      fontSize: '1rem',
      color: '#b0bec5',
    },
    body2: {
      fontSize: '0.875rem',
      color: '#b0bec5',
    },
  },
});

export default function Dashboard() {
  const [filters, setFilters] = useState({
    searchQuery: '',
  });
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [flipped, setFlipped] = useState([false, false, false]);

  const tournaments = [
    {
      name: 'Football Championship',
      status: '12 Teams Confirmed',
      date: 'Aug 20, 2024',
      time: '10:00 AM',
      location: 'New York',
      image: footballImage,
    },
    {
      name: 'Basketball Tournament',
      status: '10 Teams Confirmed',
      date: 'Sep 15, 2024',
      time: '2:00 PM',
      location: 'Los Angeles',
      image: basketballImage,
    },
    {
      name: 'Volleyball League',
      status: '8 Teams Confirmed',
      date: 'Oct 10, 2024',
      time: '5:00 PM',
      location: 'Miami',
      image: volleyballImage,
    },
  ];

  const handleFilterChange = (event) => {
    setFilters({ ...filters, [event.target.name]: event.target.value });
  };

  const handleFlip = (index) => {
    setFlipped(flipped.map((flip, i) => (i === index ? !flip : flip)));
  };

  const openGoogleMaps = (location) => {
    window.open(`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(location)}`, '_blank');
  };

  const filteredTournaments = tournaments.filter((tournament) =>
    tournament.name.toLowerCase().includes(filters.searchQuery.toLowerCase())
  );

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="fixed">
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={() => setDrawerOpen(true)}>
            <MenuIcon />
          </IconButton>
          <SportsSoccerIcon sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Sports Tournament Dashboard
          </Typography>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <FormControl sx={{ mr: 2 }}>
              <TextField
                label="Search"
                name="searchQuery"
                variant="outlined"
                value={filters.searchQuery}
                onChange={handleFilterChange}
                size="small"
                InputLabelProps={{ style: { color: '#b0bec5' } }}
                InputProps={{
                  style: { color: '#fff' },
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton>
                        <SearchIcon style={{ color: '#b0bec5' }} />
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            </FormControl>
          </Box>
        </Toolbar>
      </AppBar>
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          sx: {
            backgroundColor: '#0A0A0A',
            color: '#fff',
          },
        }}
      >
        <List sx={{ width: 250 }}>
          <ListItem button onClick={() => setDrawerOpen(false)}>
            <ListItemIcon>
              <CloseIcon style={{ color: '#fff' }} />
            </ListItemIcon>
          </ListItem>
          <ListItem button component="a" href="/home">
            <ListItemText primary="Home" />
          </ListItem>
          <ListItem button component="a" href="/about-us">
            <ListItemText primary="About us" />
          </ListItem>
          <ListItem button component="a" href="/partner">
            <ListItemText primary="Partner With Us" />
          </ListItem>
          <ListItem button component="a" href="/membership-management">
            <ListItemText primary="Membership Management" />
          </ListItem>
          <ListItem button component="a" href="/book-now">
            <ListItemText primary="Book Now" />
          </ListItem>
          <ListItem button component="a" href="/news-events">
            <ListItemText primary="News and Events" />
          </ListItem>
          <ListItem button component="a" href="/careers">
            <ListItemText primary="Careers" />
          </ListItem>
          <ListItem button component="a" href="/blogs">
            <ListItemText primary="Blogs" />
          </ListItem>
        </List>
      </Drawer>
      <Toolbar />

      <Box
        sx={{
          position: 'relative',
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          height: '80vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          textAlign: 'center',
          color: '#fff',
          width: '100%',
          animation: `${zoomAnimation} 20s infinite linear`,
        }}
      >
        <Typography variant="h1">Your Nearest Sports Community</Typography>
        <Typography variant="h5">Is Just A Tap Away</Typography>
        <Box sx={{ mt: 4 }}>
          <Button variant="contained" color="success" size="large">
            To Book Venue
          </Button>
        </Box>
      </Box>

      <Container sx={{ mt: 4 }}>
        <Typography variant="h4" gutterBottom>
          Your Tournaments
        </Typography>
        <Grid container spacing={2}>
          {filteredTournaments.map((tournament, index) => (
            <Grid item xs={12} md={6} lg={4} key={index}>
              <Card
                sx={{
                  height: '100%',
                  transformStyle: 'preserve-3d',
                  transition: 'transform 0.6s',
                  animation: flipped[index] ? `${flipAnimation} 0.6s forwards` : 'none',
                }}
              >
                <CardMedia
                  component="img"
                  height="140"
                  image={tournament.image}
                  alt={tournament.name}
                />
                <CardContent>
                  <Typography variant="h5" component="div">
                    {tournament.name}
                  </Typography>
                  <Typography variant="body1" component="div">
                    Status: {tournament.status}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    Date: {tournament.date} | Time: {tournament.time}
                  </Typography>
                  <Box sx={{ display: 'flex', alignItems: 'center' }}>
                    <Typography variant="body2" color="text.secondary" sx={{ flexGrow: 1 }}>
                      Location: {tournament.location}
                    </Typography>
                    <IconButton
                      size="small"
                      color="primary"
                      onClick={() => openGoogleMaps(tournament.location)}
                    >
                      <LocationOnIcon />
                    </IconButton>
                  </Box>
                  <Button
                    variant="contained"
                    color="primary"
                    sx={{
                      mt: 2,
                      transition: 'transform 0.3s, background-color 0.3s',
                      '&:hover': {
                        transform: 'scale(1.05)',
                        backgroundColor: '#64b5f6',
                      },
                    }}
                    fullWidth
                    onClick={() => handleFlip(index)}
                  >
                    {flipped[index] ? 'Details' : 'Register'}
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>
      </Container>
    </ThemeProvider>
  );
}
