import React, { useEffect, useState } from 'react';
import '../css/Preloader.css'; // Create this CSS file for preloader styles

const Preloader = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setLoading(false), 3000); // Adjust the timeout duration as needed
    return () => clearTimeout(timer);
  }, []);

  if (!loading) return null;

  return (
    <div id="preloader">
      <div className="ps-bg"></div>
      <div className="content">
        <div className="ps-logo-wrap">
          <div className="ps-logo-bg">
            <div className="ps-lbg-upper"></div>
            <div className="ps-lbg-lower"></div>
          </div>
          <ul className="ps-logo">
            <li data-aos="fade-up" data-aos-duration="1200" data-aos-anchor-placement="top-bottom">Let</li>
            <li data-aos="fade-up" data-aos-duration="1200" data-aos-delay="300" data-aos-anchor-placement="top-bottom">The</li>
            <li data-aos="fade-up" data-aos-duration="1200" data-aos-delay="600" data-aos-anchor-placement="top-bottom">World</li>
            <li data-aos="fade-up" data-aos-duration="1200" data-aos-delay="900" data-aos-anchor-placement="top-bottom">Play</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Preloader;
