import React, { useRef, useEffect, useState } from 'react';
import { motion, useAnimation } from 'framer-motion';
import {
  AppBar,
  Toolbar,
  Typography,
  Card,
  CardContent,
  CardMedia,
  Container,
  CssBaseline,
  Grid,
  IconButton,
  Box,
  Drawer,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Button,
} from '@mui/material';
import SportsSoccerIcon from '@mui/icons-material/SportsSoccer';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CloseIcon from '@mui/icons-material/Close';
import MenuIcon from '@mui/icons-material/Menu';
import backgroundImage from '../images/background.jpg';
import basketballImage from '../images/basketball-tournament.jpg';
import footballImage from '../images/football-championship.jpg';
import volleyballImage from '../images/volleyball-league.jpg';
import { createTheme, ThemeProvider } from '@mui/material/styles';

// Animation variants
const zoomAnimation = {
  initial: {
    backgroundSize: '100%',
  },
  animate: {
    backgroundSize: '105%',
  },
  transition: {
    duration: 20,
    repeat: Infinity,
    ease: 'linear',
  },
};

const fadeInAnimation = {
  hidden: { opacity: 0 },
  visible: { opacity: 1 },
};

const theme = createTheme({
  palette: {
    mode: 'dark',
    primary: {
      main: '#90caf9',
    },
    secondary: {
      main: '#f48fb1',
    },
    background: {
      default: '#000',
      paper: '#121212',
    },
    text: {
      primary: '#fff',
      secondary: '#b0bec5',
    },
  },
  typography: {
    h1: {
      fontSize: '3rem',
      fontWeight: 700,
      color: '#fff',
    },
    h5: {
      fontSize: '1.5rem',
      fontWeight: 400,
      color: '#fff',
    },
    body1: {
      fontSize: '1rem',
      color: '#b0bec5',
    },
    body2: {
      fontSize: '0.875rem',
      color: '#b0bec5',
    },
  },
});

const Dashboard = () => {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [expandedIndex, setExpandedIndex] = useState(null);

  const controls = useAnimation();
  const ref = useRef(null);

  // Intersection Observer
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          controls.start('visible');
        } else {
          controls.start('hidden');
        }
      },
      { threshold: 0.1 }
    );
    if (ref.current) {
      observer.observe(ref.current);
    }
    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, [controls]);

  const tournaments = [
    {
      name: 'Football Championship',
      status: '12 Teams Confirmed',
      date: 'Aug 20, 2024',
      time: '10:00 AM',
      location: 'New York',
      image: footballImage,
      details: 'Detailed description of the Football Championship.',
    },
    {
      name: 'Basketball Tournament',
      status: '10 Teams Confirmed',
      date: 'Sep 15, 2024',
      time: '2:00 PM',
      location: 'Los Angeles',
      image: basketballImage,
      details: 'Detailed description of the Basketball Tournament.',
    },
    {
      name: 'Volleyball League',
      status: '8 Teams Confirmed',
      date: 'Oct 10, 2024',
      time: '5:00 PM',
      location: 'Miami',
      image: volleyballImage,
      details: 'Detailed description of the Volleyball League.',
    },
  ];

  const handleExpand = (index) => {
    setExpandedIndex(expandedIndex === index ? null : index);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="fixed">
        <Toolbar>
          <IconButton edge="start" color="inherit" aria-label="menu" onClick={() => setDrawerOpen(true)}>
            <MenuIcon />
          </IconButton>
          <SportsSoccerIcon sx={{ mr: 2 }} />
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Sports Tournament Dashboard
          </Typography>
        </Toolbar>
      </AppBar>
      <Drawer
        anchor="left"
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        PaperProps={{
          sx: {
            backgroundColor: '#0A0A0A',
            color: '#fff',
          },
        }}
      >
        <List sx={{ width: 250 }}>
          <ListItem button onClick={() => setDrawerOpen(false)}>
            <ListItemIcon>
              <CloseIcon style={{ color: '#fff' }} />
            </ListItemIcon>
          </ListItem>
          <ListItem button component="a" href="/home">
            <ListItemText primary="Home" />
          </ListItem>
          <ListItem button component="a" href="/about-us">
            <ListItemText primary="About us" />
          </ListItem>
          <ListItem button component="a" href="/partner">
            <ListItemText primary="Partner With Us" />
          </ListItem>
          <ListItem button component="a" href="/membership-management">
            <ListItemText primary="Membership Management" />
          </ListItem>
          <ListItem button component="a" href="/book-now">
            <ListItemText primary="Book Now" />
          </ListItem>
          <ListItem button component="a" href="/news-events">
            <ListItemText primary="News and Events" />
          </ListItem>
          <ListItem button component="a" href="/careers">
            <ListItemText primary="Careers" />
          </ListItem>
          <ListItem button component="a" href="/blogs">
            <ListItemText primary="Blogs" />
          </ListItem>
        </List>
      </Drawer>
      <Toolbar />

      <motion.div
        initial="initial"
        animate="animate"
        variants={zoomAnimation}
        sx={{
          position: 'relative',
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          height: '80vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          flexDirection: 'column',
          textAlign: 'center',
          color: '#fff',
          width: '100%',
        }}
      >
        <Typography variant="h1">Your Nearest Sports Community</Typography>
        <Typography variant="h5">Is Just A Tap Away</Typography>
        <Box sx={{ mt: 4 }}>
          <Button variant="contained" color="success" size="large">
            To Book Venue
          </Button>
        </Box>
      </motion.div>

      <Container sx={{ mt: 4, overflowY: 'auto' }}>
        <Typography variant="h4" gutterBottom>
          Your Tournaments
        </Typography>
        <Grid container spacing={2}>
          {tournaments.map((tournament, index) => (
            <Grid item xs={12} md={6} lg={4} key={index}>
              <motion.div
                ref={ref}
                initial="hidden"
                animate={controls}
                variants={fadeInAnimation}
                transition={{ duration: 0.6 }}
              >
                <Card
                  sx={{
                    height: '100%',
                    perspective: '1000px',
                  }}
                >
                  <CardMedia
                    component="img"
                    height="140"
                    image={tournament.image}
                    alt={tournament.name}
                  />
                  <CardContent>
                    <Typography variant="h5" component="div">
                      {tournament.name}
                    </Typography>
                    <Typography variant="body1" component="div">
                      Status: {tournament.status}
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                      Date: {tournament.date} | Time: {tournament.time}
                    </Typography>
                    <Box sx={{ display: 'flex', alignItems: 'center', mt: 2 }}>
                      <Typography variant="body2" color="text.secondary" sx={{ flexGrow: 1 }}>
                        Location: {tournament.location}
                      </Typography>
                      <IconButton
                        size="small"
                        color="primary"
                        onClick={() => window.open(`https://www.google.com/maps/search/?api=1&query=${encodeURIComponent(tournament.location)}`, '_blank')}
                      >
                        <LocationOnIcon />
                      </IconButton>
                    </Box>
                    <Button
                      variant="contained"
                      color="primary"
                      sx={{ mt: 2 }}
                      onClick={() => handleExpand(index)}
                    >
                      {expandedIndex === index ? 'Hide Details' : 'View Details'}
                    </Button>
                    {expandedIndex === index && (
                      <motion.div
                        initial="hidden"
                        animate="visible"
                        variants={fadeInAnimation}
                        transition={{ duration: 0.6 }}
                      >
                        <Typography variant="body2" color="text.secondary" sx={{ mt: 2 }}>
                          {tournament.details}
                        </Typography>
                      </motion.div>
                    )}
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))}
        </Grid>
      </Container>
    </ThemeProvider>
  );
};

export default Dashboard;
