import { createTheme } from '@mui/material/styles';
import { blue, red, grey } from '@mui/material/colors';

const theme = createTheme({
  palette: {
    primary: {
      main: blue[800],
    },
    secondary: {
      main: red[800],
    },
    background: {
      default: grey[100],
      paper: '#fff',
    },
    text: {
      primary: blue[900],
      secondary: grey[700],
    },
  },
  typography: {
    h1: {
      fontSize: '3rem',
      fontWeight: 700,
    },
    h5: {
      fontSize: '1.5rem',
      fontWeight: 400,
    },
    body1: {
      fontSize: '1rem',
      color: grey[600],
    },
  },
});

export default theme;
